  /// Login via POST /api_login.php.
  /// Automatically tries multiple local URLs if the primary one fails.
  static Future<Map<String, dynamic>> login(String userId, String pin) async {
    final primaryUrl = await getBaseUrl();

    // List of potential local server addresses to try (Self-healing)
    final candidateUrls = [
      _emulatorUrl, // 1. Android emulator ÃƒÆ’Ã†â€™Ãƒâ€ Ã¢â‚¬â„¢ÃƒÆ’Ã¢â‚¬Å¡Ãƒâ€šÃ‚Â¢ÃƒÆ’Ã†â€™Ãƒâ€šÃ‚Â¢ÃƒÆ’Ã‚Â¢ÃƒÂ¢Ã¢â€šÂ¬Ã…Â¡Ãƒâ€šÃ‚Â¬ÃƒÆ’Ã¢â‚¬Å¡Ãƒâ€šÃ‚Â ÃƒÆ’Ã†â€™Ãƒâ€šÃ‚Â¢ÃƒÆ’Ã‚Â¢ÃƒÂ¢Ã¢â€šÂ¬Ã…Â¡Ãƒâ€šÃ‚Â¬ÃƒÆ’Ã‚Â¢ÃƒÂ¢Ã¢â€šÂ¬Ã…Â¾Ãƒâ€šÃ‚Â¢ host gateway (always works on emulator)
      _pcLanUrl, // 2. Real device on LAN
      primaryUrl, // 3. Last working URL saved in prefs
    ];

    // Remove duplicates to avoid redundant calls
    final uniqueUrls = candidateUrls.toSet().toList();
    debugPrint('ApiService: Attempting login. Candidates: $uniqueUrls');

    Map<String, dynamic> lastResult = {
      'success': false,
      'message': 'Unable to connect to server.',
    };

    for (String url in uniqueUrls) {
      if (url.isEmpty || url == 'http://' || url == 'https://') {
        debugPrint('ApiService: Skipping invalid URL candidate: "$url"');
        continue;
      }
      debugPrint('ApiService: Trying connection to $url...');

      final result = await _doLogin(url, userId, pin);
      if (result['success'] == true) {
        debugPrint('ApiService: SUCCESS connecting to $url');
        // SUCCESS! Save this working URL as the primary for future sessions
        if (url != primaryUrl) {
          await setBaseUrl(url);
        }
        return result;
      }

      debugPrint('ApiService: Failed to connect to $url: ${result['message']}');
      lastResult = result;
    }

    return lastResult;
  }

  /// Private helper for the actual HTTP call
  static Future<Map<String, dynamic>> _doLogin(
    String url,
    String userId,
    String pin,
  ) async {
    try {
      final startTime = DateTime.now();
      final response = await http
          .post(
            Uri.parse('$url/api/v1/auth/login'),
            headers: {
              'Content-Type': 'application/json',
              'Accept': 'application/json',
            },
            body: jsonEncode({
              'user_id': userId.trim(),
              'pin': pin,
              'channel': 'APP',
            }),
          )
          .timeout(
            const Duration(seconds: 5), // Fail fast to try next candidate
          );

      final duration = DateTime.now().difference(startTime).inMilliseconds;
      debugPrint(
        'ApiService: Received response from $url in ${duration}ms (Status: ${response.statusCode})',
      );

      // Guard: some PHP errors return HTML
      if (response.body.trim().startsWith('<')) {
        return {
          'success': false,
          'message': 'Server error: Received HTML instead of JSON.',
        };
      }

      final decoded = jsonDecode(response.body) as Map<String, dynamic>;

      if (response.statusCode == 200 && decoded['success'] == true) {
        final prefs = await SharedPreferences.getInstance();
        await prefs.setString(_keyToken, decoded['token'] ?? '');
        // Store user_id specifically for the profile API
        await prefs.setString(
          _keyUserId,
          decoded['data']['user_id'].toString(),
        );
        // Store only the `data` object ÃƒÆ’Ã†â€™Ãƒâ€ Ã¢â‚¬â„¢ÃƒÆ’Ã¢â‚¬Å¡Ãƒâ€šÃ‚Â¢ÃƒÆ’Ã†â€™Ãƒâ€šÃ‚Â¢ÃƒÆ’Ã‚Â¢ÃƒÂ¢Ã¢â‚¬Å¡Ã‚Â¬Ãƒâ€¦Ã‚Â¡ÃƒÆ’Ã¢â‚¬Å¡Ãƒâ€šÃ‚Â¬ÃƒÆ’Ã†â€™Ãƒâ€šÃ‚Â¢ÃƒÆ’Ã‚Â¢ÃƒÂ¢Ã¢â€šÂ¬Ã…Â¡Ãƒâ€šÃ‚Â¬ÃƒÆ’Ã¢â‚¬Å¡Ãƒâ€šÃ‚Â that is the user payload
        await prefs.setString(_keyUserData, jsonEncode(decoded['data']));
        return {'success': true, 'data': decoded};
      }

      return {
        'success': false,
        'message':
            decoded['message'] ?? 'Login failed (${response.statusCode}).',
      };
    } catch (e) {
      String errMsg = e.toString();
      if (e is http.ClientException) {
        errMsg = 'Network error (Check if server is running)';
      } else if (errMsg.contains('TimeoutException')) {
        errMsg = 'Connection timed out after 5s';
      }
      return {'success': false, 'isConnectionError': true, 'message': errMsg};
    }
  }

  /// Logout - locally clears storage
  static Future<void> logout() async {
    try {
      final prefs = await SharedPreferences.getInstance();
      await prefs.clear();
